css:
body {
  background: #456;
  font-family: 'Helvetica', sans-serif;
}

.login {
  width: 400px;
  margin: 16px auto;
  font-size: 16px;
}

/*Supprime les marges entre l'entete et le formulaire*/
.login-header,
.login p {
  margin-top: 0;
  margin-bottom: 0;
}

.login-header {
  background: #28d;
  padding: 20px;
  font-size: 1.4em;
  font-weight: normal;
  text-align: center;
  text-transform: uppercase;
  border-radius:15px 0px 0px 0px;
  color: #fff;
}

.login-container {
  background: #ebebeb;
  padding: 12px;
  border-radius:0px 0px 15px 0px;
}

.login-container div {
  padding: 12px;
  display: block;
}


.login input {
  box-sizing: border-box;
  display: inline-block;
  width: 100%;
  border-width: 1px;
  border-style: solid;
  padding: 16px;
  outline: 0;
  font-family: inherit;
  font-size: 0.95em;
}

.login input[type="email"],
.login input[type="password"] {
  background: #fff;
  border-color: #bbb;
  color: #555;
}

/* Effet sur champ de texte en Ã©dition*/
.login input[type="email"]:focus,
.login input[type="password"]:focus {
  border-color: #888;
}

.login input[type="button"] {
  background: #28d;
  border-color: transparent;
  color: #fff;
  cursor: pointer;
}

.login input[type="button"]:hover {
  background: #ADF;
}

/* Effet sur bouton en action */
.login input[type="submit"]:focus {
  border-color: #05a;
}
